            faculty = 'Fakultas Teknik';
        } else if (programStudy === 'Matematika' || programStudy === 'Fisika' || programStudy === 'Informatika') {
            faculty = 'Fakultas Teknologi Informasi dan Sains';
        }

        const newStudent = {
            fullname,
            birthDate: birthdate,
            gender,
            faculty,
            programStudy,
        };

        addStudent(newStudent);

        setFullname('');
