import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Navbar from "../components/Navbar";

const EditStudent = () => {
    const [formData, setFormData] = useState({
        fullname: "",
        profilePicture: "",
        address: "",
        phoneNumber: "",
        birthDate: "",
        gender: "",
        programStudy: "",
        faculty: "",
    });
    const [loading, setLoading] = useState(true);
    const { id } = useParams();
    const navigate = useNavigate();
    const [isDataLoaded, setIsDataLoaded] = useState(false);

    useEffect(() => {
        const fetchStudentData = async () => {
            try {
                const response = await fetch(`http://localhost:3001/student/${id}`);
                const studentData = await response.json();
                setFormData(studentData);
                setLoading(false);
                setIsDataLoaded(true);
            } catch (error) {
                console.error(error);
                setLoading(false);
                setIsDataLoaded(true);
            }
        };

        fetchStudentData();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        let updatedFaculty = "";

        if (name === "programStudy") {
            switch (value) {
                case "Ekonomi":
                case "Manajemen":
                case "Akuntansi":
                    updatedFaculty = "Fakultas Ekonomi";
                    break;
                case "Administrasi Publik":
                case "Administrasi Bisnis":
                case "Hubungan Internasional":
                    updatedFaculty = "Fakultas Ilmu Sosial dan Politik";
                    break;
                case "Teknik Sipil":
                case "Arsitektur":
                    updatedFaculty = "Fakultas Teknik";
                    break;
                case "Matematika":
                case "Fisika":
                case "Informatika":
                    updatedFaculty = "Fakultas Teknologi Informasi dan Sains";
                    break;
                default:
                    break;
            }
        }
        setFormData({
            ...formData,
            [name]: value,
            faculty: updatedFaculty,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            await fetch(`http://localhost:3001/student/${id}`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(formData),
            });
            navigate("/student");
        } catch (error) {
            console.error(error);
        }
    };

    if (!isDataLoaded) {
        return <p>Loading ...</p>;
    }

    return (
        <>
            <Navbar />
            <div className="container">
                <h1 className="text-dark mt-4 mb-4">Edit Student</h1>
                <div className="d-flex justify-content-center">
                    <img src={formData.profilePicture} alt="Profile" className="img-fluid" />
                </div>
                <form onSubmit={handleSubmit} className="text-dark">
                    <div>
                        <label htmlFor="fullname" style={{ fontWeight: 'bold' }} className="form-label fs-5">
                            Full Name:
                        </label>
                        <input
                            type="text"
                            id="fullname"
                            className="form-control form-control-lg"
                            name="fullname"
                            value={formData.fullname}
                            onChange={handleChange}
                            data-testid="name"
                            style={{
                                padding: '5px',
                                border: '1px solid #ccc',
                                borderRadius: '3px',
                                width: '99%',
                            }}
                        />
                    </div>
                    <div className="mt-2">
                        <label htmlFor="address" style={{ fontWeight: 'bold' }} className="form-label fs-5">
                            Address:
                        </label>
                        <input
                            type="text"
                            id="address"
                            className="form-control form-control-lg"
                            name="address"
                            value={formData.address}
                            onChange={handleChange}
                            data-testid="address"
                            style={{
                                padding: '5px',
                                border: '1px solid #ccc',
                                borderRadius: '3px',
                                width: '99%',
                            }}
                        />
                    </div>
                    <div className="mt-2">
                        <label htmlFor="phoneNumber" style={{ fontWeight: 'bold' }} className="form-label fs-5">
                            Phone Number:
                        </label>
                        <input
                            type="text"
                            id="phoneNumber"
                            className="form-control form-control-lg"
                            name="phoneNumber"
                            value={formData.phoneNumber}
                            onChange={handleChange}
                            data-testid="phoneNumber"
                            style={{
                                padding: '5px',
                                border: '1px solid #ccc',
                                borderRadius: '3px',
                                width: '99%',
                            }}
                        />
                    </div>
                    <div className="row row-cols-2 mt-3">
                        <div className="col-6">
                            <label htmlFor="birthDate" style={{ fontWeight: 'bold' }} className="form-label fs-5">
                                Birth Date:
                            </label>
                            <input
                                type="date"
                                id="birthDate"
                                className="form-control form-control-lg"
                                name="birthDate"
                                value={formData.birthDate}
                                onChange={handleChange}
                                data-testid="date"
                                style={{
                                    padding: '5px',
                                    border: '1px solid #ccc',
                                    borderRadius: '3px',
                                    width: '99%',
                                }}
                            />
                        </div>
                        <div className="col-6">
                            <label htmlFor="gender" style={{ fontWeight: 'bold' }} className="form-label fs-5">
                                Gender:
                            </label>
                            <select
                                id="gender"
                                className="form-control form-control-lg"
                                name="gender"
                                value={formData.gender}
                                onChange={handleChange}
                                data-testid="gender"
                                style={{
                                    padding: '5px',
                                    border: '1px solid #ccc',
                                    borderRadius: '3px',
                                    width: '99%',
                                }}
                            >
                                <option value="">Select Gender</option>
                                <option value="Male">Male</option>
                                <option value="Female">Female</option>
                            </select>
                        </div>
                    </div>
                    <div className="mt-2">
                        <label htmlFor="programStudy" className="form-label fs-5">
                            Program Study:
                        </label>
                        <select
                            id="programStudy"
                            className="form-control form-control-lg"
                            name="programStudy"
                            value={formData.programStudy}
                            onChange={handleChange}
                            data-testid="prody"
                            style={{
                                padding: '5px',
                                border: '1px solid #ccc',
                                borderRadius: '3px',
                                width: '99%',
                            }}
                        >
                            <option value="">Select Program Study</option>
                            <option value="Ekonomi">Ekonomi</option>
                            <option value="Manajemen">Manajemen</option>
                            <option value="Akuntansi">Akuntansi</option>
                            <option value="Administrasi Publik">Administrasi Publik</option>
                            <option value="Administrasi Bisnis">Administrasi Bisnis</option>
                            <option value="Hubungan Internasional">Hubungan Internasional</option>
                            <option value="Teknik Sipil">Teknik Sipil</option>
                            <option value="Arsitektur">Arsitektur</option>
                            <option value="Matematika">Matematika</option>
                            <option value="Fisika">Fisika</option>
                            <option value="Informatika">Informatika</option>
                        </select>
                    </div>
                    <button type="submit" className="btn btn-primary mt-4 fs-5" data-testid="edit-btn">
                        Edit Student
                    </button>
                </form>
            </div>
            <style>
                {`
             body {
                font-family: Arial, sans-serif;
                margin: 0;
                padding: 0;
            }

            h1 {
                margin-top: 20px;
            }

            .container {
                margin: 20px;
            }

            #table-student {
                width: 100%;
                border-collapse: collapse;
            }

            #table-student th,
            #table-student td {
                padding: 8px;
                border: 1px solid #ccc;
            }

            #table-student th {
                background-color: #f2f2f2;
                text-align: left;
            }

            .student-data-row:hover {
                background-color: #f9f9f9;
            }

            label {
