// TODO: answer here
import React, { useState } from 'react';

const AddStudent = () => {
    // TODO: answer here
    const { addStudent } = useState('');
    const [fullname, setFullname] = useState('');
    const [profilePicture, setProfilePicture] = useState("");
    const [birthdate, setBirthdate] = useState('');
    const [address, setAddress] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [gender, setGender] = useState('');
    const [programStudy, setProgramStudy] = useState('');
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();

        let faculty = '';
        if (programStudy === 'Ekonomi' || programStudy === 'Manajemen' || programStudy === 'Akuntansi') {
            faculty = 'Fakultas Ekonomi';
        } else if (programStudy === 'Administrasi Publik' || programStudy === 'Administrasi Bisnis' || programStudy === 'Hubungan Internasional') {
            faculty = 'Fakultas Ilmu Sosial dan Politik';
        } else if (programStudy === 'Teknik Sipil' || programStudy === 'Arsitektur') {
            faculty = 'Fakultas Teknik';
        } else if (programStudy === 'Matematika' || programStudy === 'Fisika' || programStudy === 'Informatika') {
            faculty = 'Fakultas Teknologi Informasi dan Sains';
        }

        const newStudent = {
            fullname,
            profilePicture,
            address,
            phoneNumber,
            birthDate: birthdate,
            gender,
            faculty,
            programStudy,
        };

        addStudent(newStudent);

        setFullname("");
        setProfilePicture("");
        setAddress("");
        setPhoneNumber("");
        setBirthdate("");
        setGender("");
        setProgramStudy("");
    };

    // CSS Styles
    const containerStyle = {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        margin: '20px 0',
    };

    const studentPortalStyle = {
        fontSize: '24px',
        fontWeight: 'bold',
    };

    const adminStyle = {
        display: 'flex',
        alignItems: 'center',
    };

    const adminTextStyle = {
        marginRight: '10px',
        fontSize: '18px',
    };

    const adminImageStyle = {
        width: '50px',
        height: '50px',
        borderRadius: '50%',
    };

    return (
        <>
            {/* TODO: answer here */}
            <div style={containerStyle}>
                <div className="student-portal" style={studentPortalStyle}>
                    Student Portal
                </div>
                <div className="admin" style={adminStyle}>
                    <div className="admin-text" style={adminTextStyle}>
                        Admin
                    </div>
                    <img
                        src="https://www.jurnalpase.com/wp-content/uploads/2019/03/admin_jurnalpase.png"
                        alt="Admin Profile"
                        style={adminImageStyle}
                    />
                </div>
            </div>
            <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                <div>
                    <label htmlFor="fullname" style={{ fontWeight: 'bold' }}>Fullname:</label>
                    <input
                        type="text"
                        id="fullname"
                        value={fullname}
                        onChange={(e) => setFullname(e.target.value)}
                        style={{
                            padding: '5px',
                            border: '1px solid #ccc',
                            borderRadius: '3px',
                            width: '99%',
                        }}
                        data-testid="name"
                    />
                </div>
                <div>
                    <label htmlFor="profilePicture" style={{ fontWeight: 'bold' }}>Profile Picture:</label>
                    <input
                        type="text"
                        id="profilePicture"
                        value={profilePicture}
                        onChange={(e) => setProfilePicture(e.target.value)}
                        style={{
                            padding: '5px',
                            border: '1px solid #ccc',
                            borderRadius: '3px',
                            width: '99%',
                        }}
                        data-testid="profilePicture"
                    />
                </div>
                <div>
                    <label htmlFor="address" style={{ fontWeight: 'bold' }}>Address:</label>
                    <input
                        type="text"
                        id="address"
                        value={address}
                        onChange={(e) => setAddress(e.target.value)}
                        style={{
                            padding: '5px',
                            border: '1px solid #ccc',
                            borderRadius: '3px',
                            width: '99%',
                        }}
                        data-testid="address"
                    />
                </div>
                <div>
                    <label htmlFor="phoneNumber" style={{ fontWeight: 'bold' }}>Phone Number:</label>
                    <input
                        type="text"
                        id="phoneNumber"
                        value={phoneNumber}
                        onChange={(e) => setPhoneNumber(e.target.value)}
                        style={{
                            padding: '5px',
                            border: '1px solid #ccc',
                            borderRadius: '3px',
                            width: '99%',
                        }}
                        data-testid="phoneNumber"
                    />
                </div>
                <div>
                    <label htmlFor="birthdate" style={{ fontWeight: 'bold' }}>Birth Date:</label>
                    <input
                        type="text"
                        id="birthdate"
                        value={birthdate}
                        onChange={(e) => setBirthdate(e.target.value)}
                        style={{
                            padding: '5px',
                            border: '1px solid #ccc',
                            borderRadius: '3px',
                            width: '99%',
                        }}
                        data-testid="date"
                    />
                </div>
                <div>
                    <label htmlFor="gender" style={{ fontWeight: 'bold' }}>Gender:</label>
                    <select
                        value={gender}
                        onChange={(e) => setGender(e.target.value)}
                        required
                        style={{
                            padding: '5px',
                            border: '1px solid #ccc',
                            borderRadius: '3px',
                            width: '99%',
                        }}
                        data-testid="gender"
                    >
                        <option value="">Select Gender</option>
                        <option value="Male">Male</option>
                        <option value="Female">Female</option>
                    </select>
                </div>
                <div>
                    <label htmlFor="programStudy" style={{ fontWeight: 'bold' }}>Program Study:</label>
                    <select
                        value={programStudy}
                        onChange={(e) => setProgramStudy(e.target.value)}
                        required
                        style={{
                            padding: '5px',
                            border: '1px solid #ccc',
                            borderRadius: '3px',
                            width: '99%',
                        }}
                        data-testid="prody"
                    >
                        <option value="">Select Program Study</option>
                        <option value="Ekonomi">Ekonomi</option>
                        <option value="Manajemen">Manajemen</option>
                        <option value="Akuntansi">Akuntansi</option>
                        <option value="Administrasi Publik">Administrasi Publik</option>
                        <option value="Administrasi Bisnis">Administrasi Bisnis</option>
                        <option value="Hubungan Internasional">Hubungan Internasional</option>
                        <option value="Teknik Sipil">Teknik Sipil</option>
                        <option value="Arsitektur">Arsitektur</option>
                        <option value="Matematika">Matematika</option>
                        <option value="Fisika">Fisika</option>
                        <option value="Informatika">Informatika</option>
                    </select>
                </div>
                <button type="submit" style={{ padding: '10px', backgroundColor: '#2B60DE', color: 'white', border: 'none', borderRadius: '3px', cursor: 'pointer' }} data-testid="submit">Submit</button>
            </form>
        </>
    );
};

export default AddStudent;
